mapper.select:
SELECT 
  a.id, a.company_id, a.title, a.description, a."location", a.max_participants, a.created_at, a.activity_date, a.start_time, a.end_time,
  (
    SELECT COALESCE(COUNT(1), 0)
    FROM public.reservations r
    WHERE r.activity_id = a.id
      AND r.status = 'confirmed'
  ) AS current_participants
FROM public.activities a


    a."location" ILIKE CONCAT('%', #{region}, '%')



      AND a.activity_date BETWEEN #{startDate} AND #{endDate}


      AND a.activity_date >= #{startDate}


      AND a.activity_date <= #{endDate}



    AND a.activity_type = #{activityType}


    AND a.max_participants - (
      SELECT COALESCE(COUNT(1), 0)
      FROM public.reservations r2
      WHERE r2.activity_id = a.id
        AND r2.status = 'confirmed'
    ) >= #{peopleCount}


ORDER BY a.created_at DESC
